import React, { useState, useEffect } from "react";
import "./styles.css";
import { WebMapView } from "./maps/WebMapView";
import { BaseMaps } from "./maps/BaseMaps";
import { DrawMapView } from "./maps/Draw";
import { Editor } from "./maps/Editor";
import { Covid19 } from "./maps/COVID19";

export default function App() {
  const [Mapitems] = useState([
    <Covid19 />,
    <Editor />,
    <BaseMaps />,
    <DrawMapView />,
    <WebMapView />
  ]);

  const [items] = useState([
    { label: "COVID-19 Map", value: 0 },
    { label: "Editor Map", value: 1 },
    { label: "Base Map Gallery", value: 2 },
    { label: "Draw Map", value: 3 },
    { label: "Simple Map View", value: 4 }
  ]);

  const [value, setValue] = useState(0);

  return (
    <>
      <div style={{ height: "3vh" }}>
        <b>React ESRI Map Examples --> </b>
        <select onChange={e => setValue(e.currentTarget.value)}>
          {items.map(item => (
            <option key={item.value} value={item.value}>
              {item.label}
            </option>
          ))}
        </select>
      </div>
      {Mapitems[value]}
    </>
  );
}
